LJ.Util.parseCurrentTextareaWord = function (textarea) {
  var selection = {
    start: textarea.selectionStart,
    end: textarea.selectionEnd
  };
  if (!selection.start || selection.start !== selection.end) {
    return null;
  }
  var content = textarea.value.replace(/\n/g, ' ');
  // Just have ended a word or started new paragraph
  if (content[selection.start - 1] === ' ' && !content[selection.start]) {
    return null;
  }
  var beforeCaret = content.substring(0, selection.start).split(' '),
      afterCaret = content.substring(selection.start).split(' ');

  if (!beforeCaret.length || !afterCaret.length) {
    return null;
  }
  var precedingHalfword = beforeCaret[beforeCaret.length - 1],
      word = precedingHalfword + afterCaret[0],
      wordStartIndex = selection.start - precedingHalfword.length;

  return {
    word: word,
    wordStartIndex: wordStartIndex,
    wordEndIndex: wordStartIndex + word.length,
    precedingHalfword: precedingHalfword
  };
};
