(function () {
  'use strict';

  /**
   * Generate a string for ljuniq cookie
   *
   * @return {String}
   */

  function generateLjUniq() {
    var alpha = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789',
        result = '',
        len = 15,
        i;

    for (i = 0; i < len; ++i) {
      result += alpha.charAt(Math.floor(Math.random() * (alpha.length - 1)));
    }

    result += ':' + Math.floor(new Date() / 1000);
    result += ':pgstats' + (Math.random() < 0.05 ? '1' : '0');

    return result;
  }

  // set ljuniq cookie, if it hasn't been set before
  if (!LJ.Cookie.get('ljuniq')) {
    LJ.Cookie.setGlobal('ljuniq', generateLjUniq(), {
      expires: 5000,
      priority: 'High'
    });
  }
})();

